styles:
.word-press-spinner {
  position: relative;
  margin: 25px auto;
  width: 30px;
  height: 30px;
  border-radius: 30px;

  -webkit-animation: inner-circle 1s linear infinite;
  animation: inner-circle 1s linear infinite;
}

.inner-circle {
  position: absolute;
  top: 5px;
  left: 5px;
  display: block;
  width: 8px;
  height: 8px;
  border-radius: 8px;
  background: #fff;
}

@-webkit-keyframes inner-circle {
  0% {
    -webkit-transform: rotate(0);
  }
  100% {
    -webkit-transform: rotate(360deg);
  }
}

@keyframes inner-circle {
  0% {
    transform: rotate(0);
    -webkit-transform: rotate(0);
  }
  100% {
    transform: rotate(360deg);
    -webkit-transform: rotate(360deg);
  }
}

